package assignments;

public class Firefox {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/* 17 july Assigment on datatypes */
		
		//1) version = 100.2  (use dataType float) 
		float firefoxversion= 100.2f;
		System.out.println("The version of Browser is :" + firefoxversion);	
		
		
		//2) name = "firefox" (use dataType String)
		String browsername= "firefox";
		System.out.println("The Name of Browser is :" + browsername);
		
		
		//3) visible = true (use dataType boolean)
		boolean Isvisible =true;
		System.out.println("Browser visibility to users ?" + Isvisible);
		
		
		//4) year = 1998 (use dataType int)
		int releasedyear =1998;
		System.out.println("The browser relased on :" + releasedyear);
		
		
		//5) logo = 'm' (Use datatype char)
		char logo= 'm';
		System.out.println("The logo of the browser :" + logo);
				
	
		
	}

}
